package Collections_Q5;

public class Item {
	
	private int id;
	private String name;
	private double price;
	
	public Item(int id, String name, double price) {
		this.id = id;
		this.name = name;
		this.price = price;
	}
	

	public String getName() {
		return name;
	}

	public double getPrice() {
		return price;
	}

	public int getId() {
		// TODO Auto-generated method stub
		return id;
	}

	
}
